const url = new URL("http://app.local.com.br/api/category");

let headers = {
    "Accept": "application/json",
    "Content-Type": "application/json",
}

fetch(url, {
    method: "GET",
    headers: headers,
})
    .then(response => response.json())
    .then(json => console.log(json));

$headers = [
    "Accept" => "application/json",
    "Content-Type" => "application/json",
];


$ch = curl_init("http://app.local.com.br/api/category");
curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

$result = curl_exec($ch);
$err = curl_error($ch);
curl_close($ch);

if ($err) {
  throw \Exception($err);
} else {
  return $response;
}

